import React, { useState, useEffect } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import { Link, useNavigate } from "react-router-dom";
import './Navbar.css';

const Navbar = () => {
    const [navbarColor, setNavbarColor] = useState('#b9a3c4'); // Initial color of navbar
    const navigate = useNavigate();

    // Function to handle scroll event
    const handleScroll = () => {
        if (window.scrollY > 100) {
            setNavbarColor('transparent'); // Change navbar color to transparent when scrolling down
        } else {
            setNavbarColor('#b9a3c4'); // Revert back to the original color when at the top
        }
    };

    // Add scroll event listener when the component mounts
    useEffect(() => {
        window.addEventListener('scroll', handleScroll);

        // Cleanup event listener when the component unmounts
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    const handleLinkClick = (e) => {
        e.preventDefault(); // Prevent the default anchor behavior
        navigate("/services"); // Redirect to services page
    };

    return (
        <div>
            <nav className="navbar navbar-expand-lg fixed-top" style={{ backgroundColor: navbarColor }}>
                <div className="container-fluid">
                    <a className="navbar-brand" href="#">Adventure Tales World</a>
                    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarSupportedContent">
                        <ul className="navbar-nav ms-auto mb-2 mb-lg-0">
                            <li className="nav-item">
                                <Link className="nav-link active" to="/home">Home</Link>
                            </li>
                            <li className="nav-item">
                                <Link className="nav-link active" to="/aboutUs">About Us</Link>
                            </li>
                            <li className="nav-item">
                                <Link className="nav-link active" to="/services" onClick={handleLinkClick}>Services</Link>
                            </li>
                            <li className="nav-item">
                                <Link className="nav-link active" to="/contactUs">Contact Us</Link>
                            </li>
                        </ul>
                    </div>
                </div>
            </nav>
        </div>
    );
};

export default Navbar;
