import React from 'react';

const services = [
  {
    title: 'Airport Transfers',
    description: 'Seamless airport transfers to and from your accommodation, ensuring a comfortable and hassle-free journey.',
  },
  {
    title: 'Tour Guide',
    description: 'Professional tour guides to help you explore every hidden gem in Georgia and Dubai with local insights.',
  },
  {
    title: 'Accommodation',
    description: 'Cozy and comfortable stays at top-rated hotels and guesthouses to suit every budget and preference.',
  },
  {
    title: 'Indian and Georgian Food',
    description: 'Delicious Indian and Georgian cuisines prepared with authentic recipes to give you a taste of home or adventure.',
  },
  {
    title: 'Sightseeing and Adventures',
    description: 'Exciting sightseeing experiences, including hiking, trekking, and cultural tours, designed to suit all adventure levels.',
  },
  {
    title: 'Vehicles for Transportation',
    description: 'A wide range of vehicles from cars to buses, ensuring reliable and comfortable transportation throughout your trip.',
  },
  {
    title: 'Private Tours as You Plan',
    description: 'Customized tours tailored to your preferences, offering the flexibility to explore at your own pace.',
  },
  {
    title: 'Stories for Your Kids',
    description: 'Engaging and fun-filled storytelling sessions for kids, creating lasting memories during family vacations.',
  },
  {
    title: 'Honeymoon Trip',
    description: 'Romantic honeymoon packages designed to make your trip unforgettable with special touches for couples.',
  },
  {
    title: 'Friends Trip',
    description: 'Adventurous group trips for friends, filled with fun activities and great experiences to share together.',
  },
  {
    title: 'Family Trip',
    description: 'Family-friendly tours with something for everyone, ensuring an enjoyable experience for all ages.',
  },
  {
    title: 'Group Tour',
    description: 'Organized group tours with like-minded travelers, providing a great way to meet new people and explore together.',
  },
  {
    title: 'Reunion',
    description: 'Specialized reunion packages designed to bring people together and create lasting memories with loved ones.',
  },
];

const Services = () => {
  const cardStyle = {
    border: '1px solid #ddd',
    padding: '20px',
    borderRadius: '10px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    transition: 'transform 0.3s ease-in-out',
    textAlign: 'center',
    height: '100%',
  };

  const cardHoverStyle = {
    transform: 'scale(1.05)',
  };

  const titleStyle = {
    fontWeight: 'bold',
    color: '#007bff',
    fontSize: '1.2rem',
  };

  const descriptionStyle = {
    fontSize: '0.95rem',
    color: '#555',
  };

  const containerStyle = {
    padding: '50px 20px',
  };

  const gridStyle = {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
    gap: '20px',
  };

  return (
    <div style={containerStyle}>
      <h2 style={{ textAlign: 'center', marginBottom: '40px' }}>Our Services</h2>
      <div style={gridStyle}>
        {services.map((service, index) => (
          <div
            key={index}
            style={cardStyle}
            onMouseEnter={(e) => (e.currentTarget.style.transform = 'scale(1.05)')}
            onMouseLeave={(e) => (e.currentTarget.style.transform = 'scale(1)')}
          >
            <h5 style={titleStyle}>{service.title}</h5>
            <p style={descriptionStyle}>{service.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Services;
