import React, { useEffect } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import AOS from 'aos';
import 'aos/dist/aos.css';
import carRental from '../../assets/car-rental.jpg';
import comfortableStays from '../../assets/comfortable-stays.jpg';
import dubaiBeauty from '../../assets/dubai-beauty.jpg';
import excitingAdventures from '../../assets/exciting-adventures.jpg';
import exploreGeorgia from '../../assets/explore-georgia.jpg';
import './Home.css';

const galleryImages = [
  { id: 1, src: exploreGeorgia, alt: 'Explore Georgia' },
  { id: 2, src: dubaiBeauty, alt: 'Beautiful Destinations' },
  { id: 3, src: comfortableStays, alt: 'Comfortable Stays' },
  { id: 4, src: excitingAdventures, alt: 'Exciting Adventures' },
  { id: 5, src: carRental, alt: 'Car Rentals' },
];

const Home = () => {
  useEffect(() => {
    AOS.init({ duration: 1000 }); // Initialize AOS with a 1-second duration
  }, []);

  return (
    <div className="content">
      {/* Carousel Section */}
      <div id="carouselExample" className="carousel slide carousel-fade" data-bs-ride="carousel" data-bs-interval="3000">
        <div className="carousel-indicators">
          {galleryImages.map((_, index) => (
            <button
              key={index}
              type="button"
              data-bs-target="#carouselExample"
              data-bs-slide-to={index}
              className={index === 0 ? "active" : ""}
              aria-current={index === 0 ? "true" : "false"}
              aria-label={`Slide ${index + 1}`}
            ></button>
          ))}
        </div>
        <div className="carousel-inner">
          {galleryImages.map((image, index) => (
            <div className={`carousel-item ${index === 0 ? "active" : ""}`} key={image.id}>
              <img src={image.src} className="d-block w-100" alt={image.alt} />
              <div className="carousel-caption d-none d-md-block">
                <h5>{image.alt}</h5>
                <p>Explore the amazing beauty of {image.alt}.</p>
              </div>
            </div>
          ))}
        </div>
        <button className="carousel-control-prev" type="button" data-bs-target="#carouselExample" data-bs-slide="prev">
          <span className="carousel-control-prev-icon" aria-hidden="true"></span>
          <span className="visually-hidden">Previous</span>
        </button>
        <button className="carousel-control-next" type="button" data-bs-target="#carouselExample" data-bs-slide="next">
          <span className="carousel-control-next-icon" aria-hidden="true"></span>
          <span className="visually-hidden">Next</span>
        </button>
      </div>
    </div>
  );
};

export default Home;
