import React, { useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import { Link, useNavigate } from "react-router-dom";
import './Navbar.css';

const Navbar = () => {
    const [isHovered, setIsHovered] = useState(false);
    const navigate = useNavigate();

    const handleMouseEnter = () => {
        setIsHovered(true);
    };

    const handleMouseLeave = () => {
        setIsHovered(false);
    };

    const handleLinkClick = (e) => {
        e.preventDefault(); // Prevent the default anchor behavior
        navigate("/services"); // Redirect to services page
    };

    return (
        <div>
            <nav className="navbar navbar-expand-lg" style={{ backgroundColor: '#2b2f36' }}>
                <div className="container-fluid">
                    <a className="navbar-brand" href="#">Adventure Tales World</a>
                    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarSupportedContent">
                        <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                            <li className="nav-item">
                                <Link className="nav-link active" to="/home" style={{co}}>Home</Link>
                            </li>
                            <li className="nav-item">
                                <Link className="nav-link active" to="/aboutUs">About Us</Link>
                            </li>
                            <li
                                className={`nav-item dropdown ${isHovered ? 'show' : ''}`}
                                onMouseEnter={handleMouseEnter}
                                onMouseLeave={handleMouseLeave}
                            >
                                <a
                                    className="nav-link dropdown-toggle"
                                    href="/services"
                                    role="button"
                                    onClick={handleLinkClick}  // Redirect to services page on click
                                    aria-expanded={isHovered ? 'true' : 'false'}
                                >
                                    Services
                                </a>
                                <ul className={`dropdown-menu ${isHovered ? 'show' : ''}`}>
                                    <li><a className="dropdown-item" href="/destinations">Destinations</a></li>
                                    <li><a className="dropdown-item" href="/car-rental">Car Rental</a></li>
                                    <li><hr className="dropdown-divider" /></li>
                                    <li><a className="dropdown-item" href="#">Something else here</a></li>
                                </ul>
                            </li>
                            <li className="nav-item">
                                {/* <a className="nav-link" aria-disabled="true">Contact Us</a> */}
                                <Link className="nav-link active" to="/contactUs">Contact Us</Link>
                            </li>
                        </ul>
                    </div>
                </div>
            </nav>
        </div>
    );
};

export default Navbar;
