import React from 'react';
import './Services.css'; 
import ServiceCard from './ServiceCard'; 
import airts from "../../assets/airport-transfers.jpg";
import accommodation from "../../assets/tour-guide";
// import tour from "../../assets/tour-guide.jpg";
// import tour from "../../assets/tour-guide.jpg";
