  import React, { useEffect } from 'react';
  import './Services.css';
  import ServiceCard from './ServiceCard';
  import { Link } from 'react-router-dom'; // Import for routing
  //pics
  import airts from "../../assets/airport-transfers.jpg";
  import accommodation from "../../assets/accomodation.jpg";
  import tour from "../../assets/tour-guide.jpg";
  import food from "../../assets/food.jpg";
  import adventure from "../../assets/adventure.jpg";
  import transport from "../../assets/transport.jpg"; // Vehicles for Transportation
  import pritours from "../../assets/private-tours.jpg";
  import kid from "../../assets/kids.jpg";
  import honey from "../../assets/honeymoon.jpg";
  import fritrip from "../../assets/friends.jpg";
  import famtrip from "../../assets/family.jpg";
  import grptour from "../../assets/group.jpg";
  import retrip from "../../assets/reunion.jpg";
  import AOS from 'aos';
  import 'aos/dist/aos.css';

  // Array of all services
  const services = [
    {
      id: 1,
      title: 'Airport Transfers',
      description: 'Seamless airport transfers to and from your accommodation, ensuring a comfortable and hassle-free journey.',
      imgSrc: `${airts}`
    },
    {
      id: 2,
      title: 'Tour Guide',
      description: 'Professional tour guides to help you explore hidden gems.',
      imgSrc: `${tour}`
    },
    {
      id: 3,
      title: 'Accommodation',
      description: 'Cozy stays at top-rated hotels and guesthouses for every budget.',
      imgSrc: `${accommodation}`
    },
    {
      id: 4,
      title: 'Indian and Georgian Food',
      description: 'Authentic Indian and Georgian cuisine to satisfy your taste buds.',
      imgSrc: `${food}`
    },
    {
      id: 5,
      title: 'Sightseeing and Adventures',
      description: 'Exciting cultural and adventure tours tailored to all levels.',
      imgSrc: `${adventure}`
    },
    {
      id: 7,
      title: 'Private Tours as You Plan',
      description: 'Personalized tours based on your preferences and schedule.',
      imgSrc: `${pritours}`
    },
    {
      id: 8,
      title: 'Stories for Your Kids',
      description: 'Fun storytelling sessions for kids to enhance their travel experience.',
      imgSrc: `${kid}`
    },
    {
      id: 9,
      title: 'Honeymoon Trip',
      description: 'Romantic honeymoon packages with special touches for couples.',
      imgSrc: `${honey}`
    },
    {
      id: 10,
      title: 'Friends Trip',
      description: 'Adventurous trips with friends filled with exciting activities.',
      imgSrc: `${fritrip}`
    },
    {
      id: 11,
      title: 'Family Trip',
      description: 'Family-friendly tours with something for everyone.',
      imgSrc: `${famtrip}`
    },
    {
      id: 12,
      title: 'Group Tour',
      description: 'Organized group tours to explore with like-minded travelers.',
      imgSrc: `${grptour}`
    },
    {
      id: 13,
      title: 'Reunion',
      description: 'Special reunion packages to create lasting memories with loved ones.',
      imgSrc: `${retrip}`
    },
  ];

  const Services = () => {
    useEffect(() => {
      AOS.init({ duration: 1000 }); // Initialize AOS with a 1-second duration
    }, []);

    return (
      <>
        {/* <div className="company-intro" data-aos="fade-up">
          <h2>About Our Services</h2>
          <p>
            We offer a variety of travel services that ensure a comfortable, enjoyable, and memorable trip.
            From airport transfers and guided tours to custom-planned vacations, weâ€™ve got you covered.
          </p>
        </div> */}

        {/* Other Services */}
        <div className="services-container">
          <h2 className="services-title" data-aos="fade-up">Our Services</h2>

          {/* Vehicles for Transportation Card */}
          <div className="vehicles-card-container" data-aos="zoom-in">
            <ServiceCard
              title="Vehicles for Transportation"
              description="A wide variety of vehicles for comfortable and reliable transportation."
              imgSrc={transport}
            />
            <Link to="/services/vehicles" className="more-button">More</Link>
          </div>

          <div className="services-grid">
            {services.map((service, index) => (
              <ServiceCard
                key={index}
                title={service.title}
                description={service.description}
                imgSrc={service.imgSrc}
                index={index}
                data-aos={index % 2 === 0 ? 'fade-left' : 'fade-right'} // Alternate animations for service cards
              />
            ))}
          </div>
        </div>
      </>
    );
  };

  export default Services;
