import React, { useState, useEffect } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import { Link, useNavigate } from "react-router-dom";
import './Navbar.css';

const Navbar = () => {
    const [navbarColor, setNavbarColor] = useState('#b9a3c4');
    const [lastScrollY, setLastScrollY] = useState(0);  // Track the last scroll position
    const [isNavbarVisible, setIsNavbarVisible] = useState(true); // Navbar visibility
    const navigate = useNavigate();

    // Handle scroll events
    const handleScroll = () => {
        if (typeof window !== "undefined") {
            if (window.scrollY > 100) {
                setNavbarColor('transparent');
            } else {
                setNavbarColor('#b9a3c4');
            }

            // Show/hide navbar based on scroll direction
            if (window.scrollY > lastScrollY) {
                // Scrolling down
                setIsNavbarVisible(false);
            } else {
                // Scrolling up
                setIsNavbarVisible(true);
            }

            setLastScrollY(window.scrollY); // Update last scroll position
        }
    };

    // Add event listener on mount, and cleanup on unmount
    useEffect(() => {
        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, [lastScrollY]); // Run the effect when the lastScrollY changes

    const handleLinkClick = (e) => {
        e.preventDefault(); // Prevent default anchor behavior
        navigate("/services"); // Redirect to services page
    };

    return (
        <div>
            <nav className={`navbar navbar-expand-lg fixed-top ${isNavbarVisible ? 'navbar-visible' : 'navbar-hidden'}`} style={{ backgroundColor: navbarColor }}>
                <div className="container-fluid">
                    <a className="navbar-brand" href="#">Adventure Tales World</a>
                    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarSupportedContent">
                        <ul className="navbar-nav ms-auto mb-2 mb-lg-0">
                            <li className="nav-item">
                                <Link className="nav-link active" to="/home">Home</Link>
                            </li>
                            <li className="nav-item">
                                <Link className="nav-link active" to="/aboutUs">About Us</Link>
                            </li>
                            <li className="nav-item">
                                <Link className="nav-link active" to="/services" onClick={handleLinkClick}>Services</Link>
                            </li>
                            <li className="nav-item">
                                <Link className="nav-link active" to="/contactUs">Contact Us</Link>
                            </li>
                        </ul>
                    </div>
                </div>
            </nav>
        </div>
    );
};

export default Navbar;
