import React from 'react';
import { useParams } from 'react-router-dom';
import './ServiceDetails.css'; // Styles specific to service details
import airts from "../../assets/airport-transfers.jpg"

const services = [
  {
    title: 'Airport Transfers',
    fullDescription: 'Enjoy smooth airport transfers with our premium transportation services...',
    imgSrc: '/assets/airport-transfers.jpg',
  },
  {
    title: 'Tour Guide',
    fullDescription: 'Our tour guides are experts in the local culture and history...',
    imgSrc: '/assets/tour-guide.jpg',
  },
  {
    title: 'Accommodation',
    fullDescription: 'We offer a range of accommodations, from budget-friendly to luxury stays...',
    imgSrc: '/assets/accommodation.jpg',
  },
  // Continue for all services...
];

const ServiceDetails = () => {
  const { id } = useParams();
  const service = services[id];

  return (
    <>
      <div className="company-intro">
        <h2>About Our Services</h2>
        <p>
          We offer a variety of travel services that ensure a comfortable, enjoyable, and memorable trip.
          From airport transfers and guided tours to custom-planned vacations, weâ€™ve got you covered.
        </p>
      </div>

      <div className="service-details-container">
        <h1 className="service-details-title">{service.title}</h1>
        <img
          src={process.env.assets + service.imgSrc}
          alt={service.title}
          className="service-details-image"
        />
        <p className="service-details-description">{service.fullDescription}</p>
      </div>
    </>
  );
};

export default ServiceDetails;
