import React from "react";
// import 'bootstrap/dist/css/bootstrap.min.css';
// import 'bootstrap/dist/css/bootstrap.dropdown.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import { Link } from "react-router-dom";
import './Navbar.css';

const Navbar = () => {
    return (
        <div>
    <nav class="navbar navbar-expand-lg bg-body-tertiary">
  <div class="container-fluid">
    <a class="navbar-brand" href="#">Adventure Tales World</a>
    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" id="navbarSupportedContent">
      <ul class="navbar-nav me-auto mb-2 mb-lg-0">
        <li class="nav-item">
        <Link className="nav-link active" to="/home">Home</Link> 

        </li>
        <li class="nav-item">
        <Link className="nav-link active" to="/aboutUs">AboutUs</Link>
        </li>
        <li
      className={`nav-item dropdown ${isHovered ? 'show' : ''}`}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <a
        className="nav-link dropdown-toggle"
        href="/services"
        role="button"
        onClick={handleLinkClick}  // Redirect to services page on click
        aria-expanded={isHovered ? 'true' : 'false'}
      >
        Services
      </a>
      <ul className={`dropdown-menu ${isHovered ? 'show' : ''}`}>
        <li><a className="dropdown-item" href="/destinations">Destinations</a></li>
        <li><a className="dropdown-item" href="/car-rental">Car Rental</a></li>
        <li><hr className="dropdown-divider" /></li>
        <li><a className="dropdown-item" href="#">Something else here</a></li>
      </ul>
    </li>
        <li class="nav-item">
          <a class="nav-link disabled" aria-disabled="true">Contact Us</a>
        </li>
      </ul>
          </div>
  </div>
</nav>
     </div>
    )
}
export default Navbar
