import React from 'react';
import { useParams } from 'react-router-dom';
import './ServiceDetails.css'; // Styles specific to service details

const services = [
  {
    title: 'Airport Transfers',
    fullDescription: 'Enjoy smooth airport transfers with our premium transportation services...',
    imgSrc: '/sr',
  },
  {
    title: 'Tour Guide',
    fullDescription: 'Our tour guides are experts in the local culture and history...',
    imgSrc: '/assets/tour-guide.jpg',
  },
  {
    title: 'Accommodation',
    fullDescription: 'We offer a range of accommodations, from budget-friendly to luxury stays...',
    imgSrc: '/assets/accomodation.jpg',
  },
  {
    title: 'Indian and Georgian Food',
    fullDescription: 'Sample authentic Indian and Georgian food prepared with fresh local ingredients...',
    imgSrc: '/assets/food.jpg',
  },
  {
    title: 'Sightseeing and Adventures',
    fullDescription: 'Join our guided tours to explore the best spots for sightseeing and adventure...',
    imgSrc: '/assets/sightseeing.jpg',
  },
  {
    title: 'Vehicles for Transportation',
    fullDescription: 'Choose from our wide selection of vehicles for easy and convenient travel...',
    imgSrc: '/assets/vehicles.jpg',
  },
  {
    title: 'Private Tours as You Plan',
    fullDescription: 'Plan your trip your way with customizable private tours...',
    imgSrc: '/assets/private-tours.jpg',
  },
  {
    title: 'Stories for Your Kids',
    fullDescription: 'Fun-filled storytelling sessions that will keep your kids entertained...',
    imgSrc: '/assets/stories.jpg',
  },
  {
    title: 'Honeymoon Trip',
    fullDescription: 'Special honeymoon packages tailored to create lasting memories for couples...',
    imgSrc: '/assets/honeymoon.jpg',
  },
  {
    title: 'Friends Trip',
    fullDescription: 'Plan the perfect getaway with friends with our adventure-packed trips...',
    imgSrc: '/assets/friends-trip.jpg',
  },
  {
    title: 'Family Trip',
    fullDescription: 'Family trips designed for fun and relaxation for all ages...',
    imgSrc: '/assets/family-trip.jpg',
  },
  {
    title: 'Group Tour',
    fullDescription: 'Join group tours and meet like-minded travelers while exploring...',
    imgSrc: '/assets/group-tour.jpg',
  },
  {
    title: 'Reunion',
    fullDescription: 'Reunion trips designed for reconnecting with loved ones and creating memories...',
    imgSrc: '/assets/reunion.jpg',
  },
];

const ServiceDetails = () => {
  const { id } = useParams();
  const service = services[id];

  return (
    <>
      <div className="company-intro">
        <h2>About Our Services</h2>
        <p>
          We offer a variety of travel services that ensure a comfortable, enjoyable, and memorable trip.
          From airport transfers and guided tours to custom-planned vacations, weâ€™ve got you covered.
        </p>
      </div>

      <div className="service-details-container">
        <h1 className="service-details-title">{service.title}</h1>
        <img src={process.env.PUBLIC_URL + service.imgSrc} alt={service.title} className="service-details-image" />
        <p className="service-details-description">{service.fullDescription}</p>
      </div>
    </>
  );
};

export default ServiceDetails;
