import React, { useEffect, useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import AOS from 'aos'; // Import AOS for animations
import carRental from '../../assets/car-rental.jpg';
import comfortableStays from '../../assets/comfortable-stays.jpg';
import dubaiBeauty from '../../assets/dubai-beauty.jpg';
import excitingAdventures from '../../assets/exciting-adventures.jpg';
import exploreGeorgia from '../../assets/explore-georgia.jpg';
import './Home.css'; // Import the CSS for styles

// Gallery images
const galleryImages = [
  { id: 1, src: exploreGeorgia, alt: 'Explore Georgia', className: 'large' },
  { id: 2, src: dubaiBeauty, alt: 'Beautiful Destinations', className: 'small' },
  { id: 3, src: comfortableStays, alt: 'Comfortable Stays', className: 'large' },
  { id: 4, src: excitingAdventures, alt: 'Exciting Adventures', className: 'medium' },
  { id: 5, src: carRental, alt: 'Car Rentals', className: 'small' },
  { id: 6, src: carRental, alt: 'Car Rentals', className: 'small' },
];

const features = [
  { title: "Free Cancellation", description: "Enjoy flexibility with free cancellation on most bookings.", icon: "🆓" },
  { title: "Secure Booking", description: "Your information is encrypted and secured with us.", icon: "🔒" },
  { title: "Health and Safety", description: "We prioritize health and safety on all our tours.", icon: "💉" },
  { title: "Trusted and Loved", description: "Highly rated by our travelers for quality services.", icon: "❤️" },
  { title: "Local Service", description: "Connect with local guides and services for a unique experience.", icon: "🌍" },
];

const Home = () => {
  useEffect(() => {
    AOS.init({ duration: 1000 }); // Initialize AOS and set default animation duration
  }, []);

  const [showMoreFAQ, setShowMoreFAQ] = useState(false); // State for showing more FAQs
  const [visibleAnswers, setVisibleAnswers] = useState([]); // State to track visible answers

  const faqQuestions = [
    { question: "What kind of clothes do I need to carry while traveling to Georgia?", answer: "You should carry clothes appropriate for the season. In winter, pack warm layers, and in summer, light clothes are sufficient, but it's always good to have a jacket for cooler evenings." },
    { question: "What is the best time to visit Georgia?", answer: "The best time to visit Georgia is between May and October when the weather is warm, and the landscapes are lush and green." },
    { question: "Do I need a visa to visit Georgia?", answer: "Citizens of many countries can enter Georgia visa-free for up to a year. However, it's best to check specific visa requirements based on your country." },
    { question: "Is Georgia a safe country to visit?", answer: "Yes, Georgia is considered a very safe country to visit. The crime rate is low, and the people are known for their hospitality." },
    { question: "What is the currency used in Georgia?", answer: "The official currency of Georgia is the Georgian Lari (GEL)." },
    { question: "Are credit cards widely accepted in Georgia?", answer: "Yes, credit cards are accepted in most hotels, restaurants, and shops in larger cities, but it's advisable to carry cash when visiting rural areas." },
    { question: "What are the best places to visit in Georgia?", answer: "Some of the top places include Tbilisi, Kazbegi, Mestia, Batumi, and the wine regions of Kakheti." },
    { question: "Can I rent a car in Georgia?", answer: "Yes, car rentals are available in major cities, and it's a great way to explore the country's stunning landscapes." },
    { question: "What languages are spoken in Georgia?", answer: "The official language is Georgian, but many people, especially in tourist areas, speak English and Russian." },
    { question: "Is tipping common in Georgia?", answer: "Tipping is not mandatory, but it is appreciated in restaurants and for services. A 10% tip is generally considered polite." },
  ];

  // Toggle visibility of answers
  const toggleAnswer = (index) => {
    setVisibleAnswers((prevVisibleAnswers) => {
      const updatedVisibleAnswers = [...prevVisibleAnswers];
      updatedVisibleAnswers[index] = !updatedVisibleAnswers[index]; // Toggle the visibility of the clicked question's answer
      return updatedVisibleAnswers;
    });
  };

  // Function to toggle the "See More" button
  const toggleShowMoreFAQ = () => {
    setShowMoreFAQ(!showMoreFAQ);
  };

  return (
    <>
      <div className="content">
        {/* Carousel Section */}
        <div id="carouselExampleIndicators" className="carousel slide" data-bs-ride="carousel" data-bs-interval="2000">
          {/* ...carousel code here... */}
        </div>

        {/* Gallery Section */}
        <section className="gallery py-5">
          <div className="container">
            <h2 className="text-center mb-4">Top Tourism Places in Georgia</h2>
            <div className="gallery-grid">
              {galleryImages.map((image) => (
                <div className={`gallery-item ${image.className}`} key={image.id}>
                  <img src={image.src} alt={image.alt} className="img-fluid" />
                  <div className="overlay">
                    <h4>{image.alt}</h4>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </section>

        {/* New Features Section */}
        <section className="features-section py-5">
          <div className="container">
            <h2 className="text-center mb-4">Why Choose Us</h2>
            <div className="row">
              {features.map((feature, index) => (
                <div key={index} className="col-md-4 col-lg-2 feature-card text-center mb-4" data-aos="fade-up">
                  <div className="feature-icon">{feature.icon}</div>
                  <h5>{feature.title}</h5>
                  <p>{feature.description}</p>
                </div>
              ))}
            </div>
          </div>
        </section>

        {/* FAQ Section */}
        <section className="faq-section py-5">
          <div className="container">
            <h2 className="text-center mb-4">Frequently Asked Questions</h2>
            <div className="faq">
              {faqQuestions.slice(0, showMoreFAQ ? faqQuestions.length : 3).map((item, index) => (
                <div className="faq-item" key={index}>
                  <button className="faq-question" onClick={() => toggleAnswer(index)}>
                    {item.question}
                  </button>
                  <div className={`faq-answer ${visibleAnswers[index] ? 'show' : ''}`}>
                    <p>{item.answer}</p>
                  </div>
                </div>
              ))}
            </div>
            {!showMoreFAQ && (
              <div className="text-center mt-4">
                <button className="btn btn-primary" onClick={toggleShowMoreFAQ}>
                  See More
                </button>
              </div>
            )}
          </div>
        </section>
      </div>
    </>
  );
};

export default Home;
